import React from "react";
import { Box, Typography, Button } from "@material-ui/core";
import styles from "./HeroBanner.module.css";
import bannerImg from "../../images/bannerImg.svg";
import { Link } from "react-router-dom";

const HeroBanner = () => {
  return (
    <div className={styles.container}>
      <Box component="div">
        <Typography className={styles.mainTitle}>
          Hello, I am <span className={styles.name}>NAME</span>
        </Typography>
        <Typography className={styles.subTitle}>
          I am a Web Developer with Expertise
          <br /> in TECHNOLOGY
        </Typography>
        <Button className={styles.btn} variant="contained">
          <Link to="/portfolio">Visit Portfolio</Link>
        </Button>
      </Box>
      <Box component="div" className={styles.imgContainer}>
        <img
          className={styles.img}
          src={bannerImg}
          alt="animated hero banner"
        />
      </Box>
    </div>
  );
};

export default HeroBanner;
